mapper.select:
select sd.*
from system_department sd
where sd.del_flag = '0'
  and sd.dept_id = #{deptId}
  and sd.parent_id = #{parentId}
  and sd.dept_name like concat('%', #{deptName}, '%')
  and sd.status = #{status}
order by sd.parent_id, sd.order_num

select sd.*
from system_department sd
where sd.dept_id = #{deptId}

select sd.*
from system_department sd
where sd.dept_name = #{deptName}
  and sd.parent_id = #{parentId}

mapper.update:
update system_department
parent_id = #{parentId}, ancestors = #{ancestors}, dept_name = #{deptName}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}, update_by = #{updateBy},
where dept_id = #{deptId}

mapper.delete:
update system_department
set del_flag = '2'
where dept_id = #{deptId}

mapper.select:
select sd.dept_id from system_department sd left join system_role_department srd on sd.dept_id = srd.dept_id where srd.role_id = #{roleId}
and sd.dept_id not in (select t_sd.parent_id from system_department t_sd inner join system_role_department t_srd on t_sd.dept_id = t_srd.dept_id and t_srd.role_id = #{roleId})
order by sd.parent_id, sd.order_num
